INSERT INTO `products` (`productID`, `categoryID`, `productName`, `productImage`, `listPrice`, `discountPercent`, `description`, `created_at`, `updated_at`) VALUES
(7, 6, 'Blade 110', 'WvWf9Ur0YzePFWfz4X6u.png', 18800000, 5, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Với tem xe mới, Blade mang một diện mạo đầy mạnh mẽ, khỏe khoắn, tạo nên phong cách thể thao và năng động cho người lái.</span><br></p>', '2021-01-31 23:24:19', '2021-01-31 23:24:19'),
(8, 6, 'Wave Alpha 110cc', 'QCYW8G9W2LImOuhy0ZkV.jpg', 17790000, 2, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Wave Alpha 110cc phiên bản 2020 trẻ trung và cá tính với thiết kế bộ tem mới, tạo những điểm nhấn ấn tượng, thu hút ánh nhìn, cho bạn tự tin ghi lại dấu ấn cùng bạn bè của mình trên mọi hành trình.</span><br></p>', '2021-01-31 23:26:39', '2021-01-31 23:26:39'),
(10, 7, 'Vision', 'Bf4EFMSz9Q70ZZj3BVm1.png', 29900000, 2, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Thuộc phân khúc xe tay ga giá thấp, Vision luôn là mẫu xe được ưa chuộng trong giới trẻ và có số lượng bán ra lớn nhất tại thị trường Việt Nam suốt nhiều năm qua nhờ kiểu dáng trẻ trung, thanh lịch và nhỏ gọn. Sau 6 năm kể từ lần thay đổi lớn gần nhất vào năm 2014, chiếc xe Vision 2020 đã được nâng cấp toàn diện cả về kiểu dáng thời trang cùng những tiện ích và công nghệ hiện đại, hứa hẹn mang đến những trải nghiệm vượt xa kỳ vọng cho những người trẻ năng động và luôn dẫn đầu xu hướng.</span><br></p>', '2021-01-31 23:32:03', '2021-01-31 23:32:03'),
(11, 7, 'Air Blade 125/150', 'ewGw2b3tdeeVP2XXEWcX.png', 41190000, 10, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Air Blade 125/150</span><br></p>', '2021-01-31 23:33:36', '2021-01-31 23:33:36'),
(12, 7, 'Sh mode 125', 'kqIIgpgsan8ksdapw80h.png', 53890000, 7, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Thuộc phân khúc xe ga cao cấp và thừa hưởng thiết kế sang trọng nổi tiếng của dòng xe SH, Sh mode luôn được đánh giá cao nhờ kiểu dáng thanh lịch, tinh tế tới từng đường nét. Lần đầu tiên sau 7 năm kể từ khi ra mắt, toàn bộ thiết kế được thay đổi toàn diện cùng với sự nâng cấp công nghệ hiện đại, Sh mode phiên bản 2020 hứa hẹn sẽ vượt xa mọi kỳ vọng về một chiếc xe thế hệ mới dành cho khách hàng.</span><br></p>', '2021-01-31 23:34:52', '2021-01-31 23:34:52'),
(13, 7, 'Lead 125 FI', 'uKojDJvvU0Ny3WJjQpf2.png', 38290000, 7, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Với thiết kế mới hiện đại, LEAD mới tôn vinh nét tinh tế, thanh lịch của người sử dụng cũng như giúp điều khiển xe thoải mái.</span><br></p>', '2021-01-31 23:35:17', '2021-01-31 23:35:17'),
(14, 8, 'Winner X', 'sqhmtk1mq0xhzewVJrBL.png', 45990000, 8, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Xứng tầm một mẫu xe côn tay thể thao cao cấp, Honda WINNER X tiếp tục khẳng định vị thế với phối màu và tem mới làm nổi bật khí chất đỉnh cao của người lái</span><br></p>', '2021-01-31 23:38:44', '2021-01-31 23:38:44'),
(16, 8, 'CB150R Exmotion', 'gIljVhXLC8RdQPxuaBKZ.jpg', 105000000, 10, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">THIẾT KẾ CB150R là thành viên trong đại gia đình Neo Sport Cafe của Honda, với sự pha trộn hoàn hảo giữa cổ điển và đương đại, nam tính và đầy bản lĩnh.</span><br></p>', '2021-01-31 23:40:00', '2021-01-31 23:40:00'),
(17, 8, 'Monkey', '3HV5kka3CZvJ6ctDbDcw.jpg', 100000000, 10, '<p>Monkey</p>', '2021-01-31 23:40:35', '2021-01-31 23:40:35'),
(18, 9, 'Rebel 500', '9GpssjZJLL5kyjqbiVRv.jpg', 180000000, 10, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Tạo nên dấu ấn của riêng mình Mẫu xe Rebel 500 với khối động cơ 2 xy lanh song song mạnh mẽ, đặt gọn trong bộ khung xe với trọng tâm thấp, phong cách thiết kế tối giản đã đưa chiếc xe trở nên hấp dẫn trong mắt người lái - là một cỗ máy thuần túy để người dùng thỏa sức sáng tạo thể hiện chất riêng của mình - đem đến một phong cách sống TỰ DO, điều mà mẫu Rebel 500 luôn mong muốn mang lại cho người lái.</span><br></p>', '2021-01-31 23:46:35', '2021-01-31 23:46:35'),
(19, 9, 'GoldWing', 'jVHTkY3JK4NyxsPuWYRd.png', 1200000000, 10, '<p><span style=\"color: rgb(33, 33, 33); font-family: Helvetica; font-size: 14px; text-align: justify;\">Điều gì đang đợi chúng ta phía sau đường chân trời? Ở bên kia thành phố nơi chúng ta sống? Hay đằng sau những điều quen thuộc mà chẳng mấy bất ngờ ? Và đâu là cách để trải nghiệm trọn vẹn nhất những điều thật mới mẻ ấy? Chúng tôi chọn cách ngao du trên những chiếc mô tô phân khối lớn nhưng linh hoạt, dễ điều khiển và mang trọn cảm hứng “chất ngất”. Hãy thử lướt trên cùng một con đường bằng xe hơi hay xe đạp, đến cùng một nhà hàng, ngắm nhìn cùng một cảnh vật rồi chia sẻ với chúng tôi đâu là chuyến đi đáng nhớ nhất. Ở phiên bản năm 2020, mẫu Gold Wing đã được cải tiến hệ thống FI và ly hợp kép DCT giúp cải thiện khả năng vận hành ở tốc độ thấp, hệ thống giảm xóc được tinh chỉnh cùng tay nắm cho người ngồi sau được thiết kế to hơn, mang lại sự thoải mái khi vận hành. Ngoài ra, mẫu xe Gold Wing mới cũng được trang bị thêm cho người dùng 1 cổng sạc USB cùng đèn sương mù.</span><br></p>', '2021-01-31 23:47:31', '2021-01-31 23:47:31'),
(20, 10, 'Bộ Nhông, Xích', 'lpuCfmgOCmy4meXMDFCr.png', 165000, 0, '<p><span style=\"font-family: Helvetica, sans-serif; font-size: 14px; text-align: justify;\">Tuổi thọ của xích tải phụ thuộc vào việc điều chỉnh va bôi trơn hợp lý. Bảo dưỡng không tốt dẫn đến hư hỏng xích tải và nhông xích. Honda khuyến cáo bạn nên kiểm tra và bôi trơn định kì mỗi 500km và thay thế theo tư vấn của kỹ thuật viên để đảm bảo xe của bạn được vận hành an toàn.</span><br></p>', '2021-01-31 23:50:34', '2021-01-31 23:50:34'),
(21, 10, 'Tấm lọc gió', 'IuMc99XSVWuHCu2O6LnY.png', 65000, 0, '<p><span style=\"font-family: Helvetica, sans-serif; font-size: 14px; text-align: justify;\">Lọc gió có chức năng lọc không khí đi vào động cơ và giữ lại bụi bẩn, đảm bảo không khí đi vào động. Lọc gió không được kiểm tra và vệ sinh sẽ làm gia tăng việc tiêu hao nhiên liệu và giảm tuổi thọ các phụ tùng khác. Lọc gió khô cần được vệ sinh sau mỗi 4000km và thay thế sau mỗi 12000km. Đối với lọc gió dầu, không được cố gắng làm sạch mà cần thay thế sau mỗi 16000km với xe bảo hành 2 năm, mỗi 18000km với xe bảo hành 3 năm để đảm bảo xe được vận hành an toàn.</span><br></p>', '2021-01-31 23:51:18', '2021-01-31 23:51:18'),
(22, 10, 'Bugi', 'UH9foJbR9vdARzbsUEkz.jpg', 57000, 0, '<p><span style=\"font-family: Helvetica, sans-serif; font-size: 14px; text-align: justify;\">Honda khuyến cáo cứ 8000 km bạn nên thay thế định kì 1 lần để đảm bảo xe của bạn được vận hành an toàn, riêng với Airblade và PCX 2016, sau 12000 km bạn nên thay thế định kì 1 lần.</span><br></p>', '2021-01-31 23:52:03', '2021-01-31 23:52:03'),
(23, 10, 'Bộ má phanh', 'Vk29zWeOYRWZQsFd0Rnz.jpg', 66000, 0, '<p><span style=\"font-family: Helvetica, sans-serif; font-size: 14px; text-align: justify;\">Hệ thống phanh giữ vai trò là hệ thống an toàn, nếu duy trì tình trạng xấu trong thời gian dài có thể làm lây lan hư hỏng sang các chi tiết và bộ phận liên quan, trường hợp xấu có thể gây ra tai nạn. Honda khuyến cáo cần kiểm tra má phanh mỗi 4000km với xe bảo hành 2 năm hoặc mỗi 6000km với xe bảo hành 3 năm để đảm bảo xe được vận hành an toàn.</span><br></p>', '2021-01-31 23:52:29', '2021-01-31 23:52:29'),
(24, 11, 'Mũ không kính Matt- Đen', 'o7Z3SWBydp6etc1cNsbg.jpg', 125000, 0, '<p>Mũ không kính - Matt Đen</p>', '2021-01-31 23:57:52', '2021-01-31 23:57:52'),
(25, 11, 'Thảm Lót chân', '9CknCJEfz20Zy6GkdYYu.jpg', 240000, 0, '<p>Thảm</p>', '2021-01-31 23:58:48', '2021-01-31 23:59:30'),
(26, 11, 'Ốp mặt nạ trước', 'rM6U1UFtfr0RfH9rkHKl.jpg', 270000, 0, '<p>ốp</p>', '2021-01-31 23:59:16', '2021-01-31 23:59:16'),
(27, 11, 'Ốp tay lái sau', 'gDULBxDt3bcFATv5E1fc.jpg', 210000, 0, '<p>Ốp</p>', '2021-01-31 23:59:51', '2021-01-31 23:59:51');

